function burgerMenu(){const e=document.querySelector(".burger"),s=document.querySelector(".menu"),t=document.querySelector("body"),c=document.querySelector(".nav__btns"),o=document.querySelector(".section__hero");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),c.classList.remove("active"),o.classList.remove("all")):(s.classList.add("active"),e.classList.add("active-burger"),t.classList.add("locked"),c.classList.add("active"),o.classList.add("all"))})),window.addEventListener("resize",(()=>{window.innerWidth>991.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),t.classList.remove("locked"),c.classList.remove("active"),o.classList.remove("all"))}))}function submenu(){const e=document.querySelector(".submenu"),s=document.querySelector(".with__submenu"),t=document.querySelector(".submenu__arrow");s.addEventListener("click",(()=>{e.classList.toggle("active"),t.classList.toggle("active")}))}function myFunction(){document.getElementById("myDropdown").classList.toggle("show")}burgerMenu(),submenu(),window.onclick=function(e){if(!e.target.matches(".dropbtn")){var s,t=document.getElementsByClassName("dropdown-content");for(s=0;s<t.length;s++){var c=t[s];c.classList.contains("show")&&c.classList.remove("show")}}};